mapper.select:
SELECT COUNT(*)
FROM HOBBYCLASS
WHERE USER_ID = #{userId}

SELECT CLASS_NO, USER_ID, CLASS_TITLE, CLASS_IMAGE, CLASS_STATE, CLASS_INTRO, CLASS_PRICE, CATEGORY, KIT_NAME, KIT_PRICE, KIT_IMAGE, KIT_INTRO
FROM HOBBYCLASS
WHERE CLASS_NO = #{hobbyClassNo}

mapper.update:
UPDATE HOBBYCLASS
CLASS_TITLE = #{hobbyClassName:VARCHAR}, CATEGORY = #{category:CHAR}, CLASS_PRICE = #{hobbyClassPrice:NUMERIC}, CLASS_IMAGE = #{hobbyClassImage:VARCHAR}, CLASS_INTRO = #{hobbyClassIntro:CLOB}, START_DATE = SYSDATE
WHERE CLASS_NO = #{hobbyClassNo}

UPDATE HOBBYCLASS
KIT_NAME = #{kitName:VARCHAR}, KIT_PRICE = #{kitPrice:NUMERIC}, KIT_IMAGE = #{kitImage:VARCHAR}, KIT_INTRO = #{kitIntro:VARCHAR}, START_DATE = SYSDATE
WHERE CLASS_NO = #{hobbyClassNo}

UPDATE HOBBYCLASS
CLASS_STATE = 1, START_DATE = SYSDATE
WHERE CLASS_NO = #{hobbyClassNo}

mapper.select:
SELECT NICKNAME
FROM USERS
WHERE USER_ID = #{userIdValue}

mapper.update:
UPDATE LESSON
LESSON_TITLE = #{lessonTitle:VARCHAR}, LESSON_IMAGE = #{lessonImage:VARCHAR}, LESSON_INTRO = #{lessonIntro:VARCHAR}, LESSON_CONTENT = #{lessonContent:CLOB}, LESSON_PROJECT = #{lessonProject:VARCHAR}, LESSON_VIDEO = #{lessonVideo:VARCHAR}
WHERE LESSON_NO = #{lessonNo}

mapper.select:
SELECT LESSON_NO, CLASS_NO, LESSON_TITLE, LESSON_IMAGE, LESSON_INTRO, LESSON_CONTENT, LESSON_PROJECT, LESSON_VIDEO
FROM LESSON
WHERE LESSON_NO = #{lessonNo}

SELECT HASHTAG_CODE
FROM HASHTAG
WHERE CLASS_NO = #{hobbyClassNo}

SELECT SUM(PRICE) TOTAL_MONEY
FROM PURCHASE
WHERE CLASS_NO = #{hobbyClassNo}

SELECT COUNT(CLASS_NO)
FROM LESSON
WHERE CLASS_NO = #{hobbyClassNo}

mapper.update:
UPDATE HOBBYCLASS
CLASS_STATE = '2', START_DATE = SYSDATE, END_DATE = SYSDATE +1
WHERE CLASS_NO = #{hobbyClassNo}
