mapper.update:
UPDATE community
community_content = #{replyContent}, reg_date = to_char( SYSDATE, 'YYYY-MM-DD HH24:MI')
WHERE community_no = #{replyNo}

mapper.select:
 
	    select NVL(count(*), 0)
    	from ( 
    			select *
    			from community
    			WHERE ref_no = #{articleNo}
    			and community_type = '1'	)
